<script setup lang="ts">
import { ref, onMounted } from 'vue';
import { useRoute } from 'vue-router'
const isDesktop = ref(false);

onMounted(() => {

    isDesktop.value = window.innerWidth >= 768;

    window.addEventListener('resize', () => {
        isDesktop.value = window.innerWidth >= 768;
    });
});
const imageVisible = ref(false);

onMounted(() => {

    setTimeout(() => {
        imageVisible.value = true;
    }, 4000)
});
const scrollPosition = ref(0);
const navbarClass = ref('gradientbg py-5');
const buttonClass = ref('pe-10')
const buttonPadding = ref('py-3 px-5 border-[1px] border-[#FFFFFF] bg-transparent')
const handleScroll = () => {
    scrollPosition.value = window.scrollY;
    if (scrollPosition.value > 50) {
        navbarClass.value = 'bg-[#071C2A] py-0';
        buttonClass.value = 'pe-0';
        buttonPadding.value = 'bg-[#135E95] py-5 px-10 border-[1px] border-[#135E95]'

    } else {
        navbarClass.value = 'gradientbg py-5';
        buttonClass.value = 'pe-10';
        buttonPadding.value = 'py-3 px-5 border-[1px] border-[#FFFFFF] bg-transparent'
    }
};

onMounted(() => {
    window.addEventListener('scroll', handleScroll);
});
const route = useRoute()

const isActive = (path) => route.path === path
</script>

